import React , {useState} from "react";
import './skill.css';
import arrow from '../../images/arrow-right.png';
import logo1 from '../../images/Htmlcss.png';
import logo2 from '../../images/java-script.png';
import logo3 from '../../images/react.png';
import logo4 from '../../images/nodejs.png';
export default function Skill(){

    const [curslide,setSlide] = useState(0);
    const arr=[
       {
           id: 1,
           title: "HTML-CSS",
           level : "Intermediate",
           img : logo1
       },
       {
           id: 2,
           title: "JavaScript",
           level : "Intermediate",
           img : logo2
       },
       {
           id: 3,
           title: "React",
           level : "Intermediate",
           img : logo3
       },
       {
           id: 4,
           title: "NodeJs",
           level : "Beginner",
           img : logo4
       },
       {
           id: 5,
           title: "Express",
           level : "Beginner",
           img : logo1
       },
       {
           id: 6,
           title: "MongoDB",
           level : "Beginner",
           img : logo1
       }
      ]

    return(
        <>
        <div className="skills" id="skill">
            <h1 className="head2" data-text="Skills...">Skills...</h1>
             <div className="arrow">
                    <div className="left">
                     <img src={arrow} onClick={()=>{
                       var result = (curslide>0)?curslide-1: arr.length-1;
                       setSlide(result);
                     }} />
                    </div>
                    <div className="img-right">
                    <img src={arrow} onClick={()=>{
                       var result = (curslide>=0 && curslide<5 )?curslide+1:0;
                       setSlide(result);
                     }} />
                    </div>
                </div>
                <div className="wrapper" style={{left:`-${curslide*100}%`}}>
                {
                    arr.map(item =>{
                          return(
                             <>
                              <div className="container " >   
                                <div className="items">    
                                   <div className="left">
                                     <h4>Skill :- {item.title}</h4>
                                     <h5>Level :- {item.level}</h5>
                                     <p>Check Projects <br>
                                     </br>
                                        <button>Check</button>
                                       </p>
                                     </div>
                                     <div className="right">
                                     <img src={item.img} alt="" />
                                     </div>
                                </div>     
                             </div>
                    
                              </>
                          )
                    })
                }
            </div>
            </div>
        </>
    );
}
