import React, { useState } from 'react';


export default function Content(props){
    console.log(props.id);

    return(
        <>
           <div className={"content "+(props.cl && "active2")} id={props.id} style={{
               width:"15%",
               height:"8vh",
               border:"1px solid gray",
               display:"flex",
               justifyContent:"center",
               alignItems:"center",
               fontSize:"15px",
               fontWeight:"800",
               borderRadius:"10px",
               cursor:"pointer",
               marginRight:"20px",
               transition:"all 0.6s ease"
           }} onClick={()=>{
               if(props.id === 1){
                   document.getElementById(props.id-1).classList.remove("active2");
                   document.getElementById(props.id).classList.add("active2");
                   document.getElementById(props.id+1).classList.remove("active2");
                   document.getElementById("item1").style.left="0";
                   document.getElementById("item2").style.left="100vw";
                   document.getElementById("item3").style.left="200vw";
               }
               else if(props.id === 2){
                   document.getElementById(props.id-2).classList.remove("active2");
                   document.getElementById(props.id-1).classList.remove("active2");
                   document.getElementById(props.id).classList.add("active2");
                   document.getElementById("item1").style.left="0";
                   document.getElementById("item2").style.left="200vw";
                   document.getElementById("item3").style.left="100vw";
               }
               else {
                   document.getElementById(props.id+2).classList.remove("active2");
                   document.getElementById(props.id+1).classList.remove("active2");
                   document.getElementById(props.id).classList.add("active2");
                   document.getElementById("item1").style.left="100vw";
                   document.getElementById("item2").style.left="0";
                   document.getElementById("item3").style.left="200vw";
               }
           }}>
                 {props.text}
           </div>
        </>
    )
}
