import React from 'react';


export default function Main(props){
    const w=window.outerWidth;
     var h1=0,txt=0,cl="white";
     if(w>500)
     {
         h1=50;
         txt=20;
     }
     else{
         h1=200;
         txt=8;
         cl="black";
     }

    return(
        <>
        <div className="main-file" style={{
            width:"300px",
            height:`${h1}px`,
            border:"1px solid lightgray",
            display:"flex",
            flexDirection:"column",
            justifyContent:"center",
            alignItems:"center",
            borderRadius:"10px",
            fontSize:`${txt}px`,
        }}>

            <h2 style={{color:`${cl}`}}>{props.text}</h2>
            <img src={props.image} alt="" style={{
                width:"80%",
                height:"80%"
            }}/>
        </div>
        </>
    )
}
