import React, { useEffect } from 'react';
import './Intro.css';
import Card from './Card';
import img1 from '../../images/one.jpeg';
import img2 from '../../images/two.jpeg';
import img3 from '../../images/two.png';
import { useState } from 'react';

export default function Intro(){
  var a;
 
  const [p,setp] = useState(0);

  useEffect(()=>{
    a = setTimeout(()=>{
      if(p === -300)
      {
        clearTimeout(a);
        setp(0);
      }
      else{
        setp(p-100);
      }
    },5000)
  })

  const head=["Director's Message","Principal's Message","Vice Principal's Message"];
  const name=["Niyajuddin Ansari","Najma Nesha","Khaliq Ansari "];
  const text=["जब भी पढ़ाई करने बैठे ये सोच कर बैठे कि आखिरी बार पढ़ रहा हू कल मेरा एग्जाम है यकिनन इस सोच के साथ की गयी तैयारी एक अलग लेवल की होगी और आप बड़े से बड़ा एग्जाम असानी से पास कर लेंगे ।",
              " रिजल्ट पे ध्यान देकर काम करोगे तो काम नहीं बनेगा ,तो ध्यान अपने उस छोटे छोटे कदम पर दो जो आप रोज उस रिजल्ट को पाने के लिए लेते हो,यही चीज आपकी आपके रिजल्ट तक लेके जायेगी |", "जब भी पढ़ाई करने बैठे ये सोच कर बैठे कि आखिरी बार पढ़ रहा हू कल मेरा एग्जाम है यकिनन इस सोच के साथ की गयी तैयारी एक अलग लेवल की होगी और आप बड़े से बड़ा एग्जाम असानी से पास कर लेंगे ।"];

  return(
    <>
         <div className="i">
           <div className="i-wrapper">
              <div className="card1" id='1' style={{left:`${p}vw`}}>
                <img src={img1} alt="" />
              </div>
              <div className="card2" id='2' style={{left:`${p+100}vw`}}>
                 <Card head={head[0]} name={name[0]} text={text[0]} img={img3} />
              </div>
              <div className="card3" id='3' style={{left:`${p+200}vw`}}>
                 <Card head={head[1]} name={name[1]} text={text[1]} />
              </div>
              <div className="card4" id='4' style={{left:`${p+300}vw`}}>
                 <Card head={head[2]} name={name[2]} text={text[2]} img={img2}/>
              </div>
           </div>
         </div>
    </>
  )
}
