import React from 'react';
import './Button.css';

export default function Button(props){

    var a="0",b="0";

    const sum = (num1,num2,opr)=>{
          var res = 0;
         if(opr === "+")
         res=num1+num2;
         else if(opr==="-")
         res=num1-num2;
         else if( opr === "×")
         res=num1*num2;
         else if( opr === "÷")
         res=num1/num2;
         else if( opr === "%")
         {
             if(num2 === 0)
             res= num1 /100;
             else
             res = (num2*num1)/100;
         }
         else{
             res = -(num1);
         }

         return res;
    }
   const seperator = (string)=> {
       let res="",finalRes=0,opr="";
       var num1=0,num2=0;
     let arr = Array.from(string);
     arr.map((item,i) =>{
         if(item === "+"|item === "-"|item === "×"|item === "÷"|item === "%" && i!=0){
             num1 = parseFloat(res);
              res="";
              opr=item;
         }
         else if(item === "=")
         {
             num2 = parseFloat("0"+res);
           finalRes =  sum(num1,num2,opr);
         }
         else if(item === "-" | item==="±" && i===0)
         res = "-";
         else
             res=res+item;
     })
     num2=0;
       return finalRes;
   }


    return(
        <>
            <div className="btn-layer">
                 <div className="btn" id={props.text1} style={{color:`${props.color}`}} onClick={() =>{
                    a =  document.getElementById(`${props.text1}`).innerHTML;
                    b = document.getElementById('top').innerHTML;
                   document.getElementById('top').innerHTML = b+a;
                   if(props.text1 === "C")
                   {
                    document.getElementById('top').innerHTML = "" ;
                   }
                 }}>{props.text1}</div>
                 <div className="btn" id={props.text2}  style={{color:`${props.color}`}} onClick={() =>{
                     a = document.getElementById(`${props.text2}`).innerHTML;
                     b = document.getElementById('top').innerHTML;
                    document.getElementById('top').innerHTML =  b+a;
                 }}>{props.text2}</div>
                 <div className="btn" id={props.text3}  style={{color:`${props.color}`}} onClick={() =>{
                     a= document.getElementById(`${props.text3}`).innerHTML;
                     b = document.getElementById('top').innerHTML;
                    document.getElementById('top').innerHTML = b+a;
                 }}>{props.text3}</div>
                 <div className="btn" id={props.text4}  style={{color:`${props.colorText}`,backgroundColor:`${props.bg}`}} onClick={() =>{
                     a= document.getElementById(`${props.text4}`).innerHTML;
                     b = document.getElementById('top').innerHTML;
                    document.getElementById('top').innerHTML = b+a;
                    if(props.text4 === "="){
                    document.getElementById('top').innerHTML= b+"<br>"+"="+seperator(b+a)+"<br>";
                    b="0";
                    console.log(b);
                }
                 }}>{props.text4}</div>
            </div>
        </>
    )
}
