import React, {useState} from 'react';
import './Button.css';

export default function Button(props){
    var a=""; b="";

    var [state,chState] = useState(true);

function show1()
{
    var l1 = document.getElementById(`${props.text1}`);
    a=l1.innerHTML;
    if(state === true)
   {
       if(a === "C"){
           b=document.getElementById("med").innerHTML="";
           b=document.getElementById("top").innerHTML="";
       }
       
       else
    { b=document.getElementById("med").innerHTML;
    document.getElementById("med").innerHTML = b+a;}}
    else{
     document.getElementById("med").innerHTML = "";
    chState(true);
}
}
function show2()
{
    var l2 = document.getElementById(`${props.text2}`);
    a=l2.innerHTML;
    if(state === true)
   { b=document.getElementById("med").innerHTML;
    document.getElementById("med").innerHTML = b+a;}
    else{
     document.getElementById("med").innerHTML = "";
     chState(true);}
}
function show3()
{
    var l3 = document.getElementById(`${props.text3}`);
    a=l3.innerHTML;
    if(state === true)
    { b=document.getElementById("med").innerHTML;
     document.getElementById("med").innerHTML = b+a;}
     else{
         document.getElementById("med").innerHTML = "";
        chState(true);}
}
function show4()
{
    var l4 = document.getElementById(`${props.text4}`);
    a=l4.innerHTML;
    b=document.getElementById("med").innerHTML;
    if(props.text4 === "="){
    document.getElementById("top").innerHTML = seperator(b+a);
    document.getElementById("med").innerHTML = b;
    chState(false);
    console.log(state);
    }
    else
    document.getElementById("med").innerHTML=b+a;
     
}


    var a="0",b="0";

    const sum = (num1,num2,opr)=>{
          var res = 0;
         if(opr === "+")
         res=num1+num2;
         else if(opr==="-")
         res=num1-num2;
         else if( opr === "×")
         res=num1*num2;
         else if( opr === "÷")
         res=num1/num2;
         else if( opr === "%")
         {
             if(num2 === 0)
             res= num1 /100;
             else
             res = (num2*num1)/100;
         }
         else{
             res = -(num1);
         }

         return res;
    }
   const seperator = (string)=> {
       console.log(string);
       let res="",finalRes=0,opr="";
       var num1=0,num2=0;
     let arr = Array.from(string);
     arr.map((item,i) =>{
         if(item === "+"|item === "-"|item === "×"|item === "÷"|item === "%" && i!==0){
             num1 = parseFloat(res);
              res="";
              opr=item;
         }
         else if(item === "=")
         {
             num2 = parseFloat("0"+res);
           finalRes =  sum(num1,num2,opr);
         }
         else if(item === "-" | item==="±" && i===0)
         res = "-";
         else
             res=res+item;
     })
     num2=0;
     num1=0;
       return finalRes;
   }

    return(
        <>
            <div className="btn-layer">
                 <div className="btn" id={props.text1} style={{color:`${props.color}`}} onClick={show1}>{props.text1}</div>
                 <div className="btn" id={props.text2}  style={{color:`${props.color}`}} onClick={show2}>{props.text2}</div>
                 <div className="btn" id={props.text3}  style={{color:`${props.color}`}} onClick={show3}>{props.text3}</div>
                 <div className="btn" id={props.text4}  style={{color:`${props.colorText}`,backgroundColor:`${props.bg}`}} onClick={show4}>{props.text4}</div>
            </div>
        </>
    )
}
