import React, { useState } from "react";
import "./SideBar.css";
import HouseIcon from '@mui/icons-material/House';
import SearchIcon from '@mui/icons-material/Search';
import StarOutlineIcon from '@mui/icons-material/StarOutline';
import QueueMusicIcon from '@mui/icons-material/QueueMusic';
import AddCircleOutlinedIcon from '@mui/icons-material/AddCircleOutlined';

export default function SideBar(){

    const [home,setHome]= useState(false);
    const [search,setSearch]= useState(false);
    const [fav,setfav]= useState(false);
    const [play,setPlay]= useState(false);
    function show(){
        const url = window.location.href;
        if(url === "/search"){   
        setSearch(true);
        }
        else if(url === "/playlist")
        setPlay(true);
        else if(url === "/")
        setHome(true);
        else if(url === "/Crplaylist")
        setHome(true);
        else
        setfav(true);
    } 

    return(
        <>
           <div className="s">
               <div className="s-items">
                   <div className={home ? "s-home active" : "s-home"}>
                      <HouseIcon sx={{color:"white"}} fontSize="large"/> 
                      <a href="/" classname="s-diff" onClick={show}>Home</a>
                   </div>
                   <div className={search ? "s-search active":"s-search"}>
                       <SearchIcon fontSize="large"/> 
                       <a href="/search" classname="s-diff" onClick={show}>Search</a>
                    </div>
                   <div className={fav ? "s-fav active":"s-fav"}>
                         <StarOutlineIcon fontSize="large"/> 
                         <a classname="s-diff" onClick={show}>Favourites</a> 
                   </div>
                   <div className={play ?"s-playlist active ":"s-playlist"}>
                         <QueueMusicIcon fontSize="large"/> 
                         <a href="/playlist" classname="s-diff" onClick={show}>PlayList</a>
                   </div>
                   <div className="s-addplist">
                        <AddCircleOutlinedIcon fontSize="large"/> <a href="/Crplaylist" classname="s-diff" onClick={show}>Create Playlist</a>
                   </div>
               </div>
           </div>
        </>
    );
}
