import React, { useState } from 'react';


export default function Content(props){
    console.log(props.id);

    return(
        <>
           <div className={"content "+(props.cl && "active")} id={props.id} style={{
               width:"9%",
               height:"8vh",
               border:"1px solid gray",
               display:"flex",
               justifyContent:"center",
               alignItems:"center",
               fontSize:"15px",
               fontWeight:"800",
               borderRadius:"10px",
               cursor:"pointer",
               marginRight:"20px",
               transition:"all 0.6s ease"
           }} onClick={()=>{
               if(props.id === 1){
                   document.getElementById(props.id).style.backgroundColor="rgb(2,2,66)";
                   document.getElementById(props.id).style.color="white";
                   document.getElementById(props.id-1).style.backgroundColor="white";
                   document.getElementById(props.id-1).style.color="black";
               }
           }}>
                 {props.text}
           </div>
        </>
    )
}
