import React, { useRef } from "react";

export default function Latest(){

    const audioElm = useRef();

    return(
        <>
           <div className="song">
            <h3>Now Playing</h3>
            <audio src=".../hello.wav" autoPlay controls ref={audioElm}></audio>
            <button onClick={()=>{
                audioElm.current.play();
            }}>Play</button>
           </div>
        </>
    )
}
