import React, { useState } from "react";
import "./SideBar.css";
import HouseIcon from '@mui/icons-material/House';
import SearchIcon from '@mui/icons-material/Search';
import StarOutlineIcon from '@mui/icons-material/StarOutline';
import QueueMusicIcon from '@mui/icons-material/QueueMusic';
import AddCircleOutlinedIcon from '@mui/icons-material/AddCircleOutlined';

export default function SideBar(){

    const [home,setHome]= useState(true);
    const [search,setSearch]= useState(true);
    const [fav,setfav]= useState(false);
    const [play,setPlay]= useState(false);
    function show(id){
        if(id === 0)
        {
            setHome(true);setSearch(false);setPlay1(false);setfav(false);
        }
    }

    return(
        <>
           <div className="s">
               <div className="s-items">
                   <div className={home ? "s-home active" : "s-home"}>
                      <HouseIcon sx={{color:"white"}} fontSize="large"/> 
                      <a classname="s-diff" onClick={show(0)}>Home</a>
                   </div>
                   <div className={search ? "s-search active":"s-search"}>
                       <SearchIcon fontSize="large"/> <a classname="s-diff">Search</a>
                    </div>
                   <div className="s-fav">
                         <StarOutlineIcon fontSize="large"/> <a classname="s-diff">Favourites</a> 
                   </div>
                   <div className="s-playlist">
                         <QueueMusicIcon fontSize="large"/> <a classname="s-diff">PlayList</a>
                   </div>
                   <div className="s-addplist">
                        <AddCircleOutlinedIcon fontSize="large"/> <a classname="s-diff">Create Playlist</a>
                   </div>
               </div>
           </div>
        </>
    );
}
