import React, { useState } from "react";
import AddIcon from '@mui/icons-material/Add';
import SimplePaper from './Note';

export default function Content(){

    const [title,upTitle] = useState();
    const [titleArr,uptitleArr] = useState([]);
    const [text,upText] = useState();
    const [textArr,uptextArr] = useState([]);

    function showTitle(e){
        upTitle(e.target.value);
    }
    function showText(e){
        upText(e.target.value);
    }

return(
    <>
    <div className="main">
        <div className="textArea">
            <div className="textContent">
    <input type="text" name="" id="title" placeholder="Title" onChange={showTitle} />
    <textarea name="" id="" cols="20" rows="8" placeholder="Enter some text" onChange={showText} ></textarea>
    </div>
    <button className="btn">
        <AddIcon />
    </button>
    </div>
    </div>
    <div className="notes">
    < SimplePaper t1={title} t2={text} />
    </div>
    </>
);
};
