import React, { useState } from "react";
import './project.css';
import Card from '../Card/Card';
import img1 from '../../images/Pr-1.png';
import img2 from '../../images/Pr-2.png';
import img3 from '../../images/Pr-3.png';
import img4 from '../../images/Pr-4.png';
import img5 from '../../images/Pr-5.png';
export default function Projects(){

    const arr=["Typing_test","Love Calculator","Random Quotes","Basic Calculator","Notes App","pr-6","pr-7","pr-8"];
    const [getActive1,setActive1] = useState(true);
    const [getActive2,setActive2] = useState(false);
    const [getActive3,setActive3] = useState(false);

    return(
        <>
        <div className="project" id="projects">
              <div className="project-container">
                      <div className="layer1">
                          <h1>Projects</h1>
                      </div>
                      <div className="layer2">
                          <div className={"content1 " + (getActive1 && "active1")} onClick={()=>{
                             document.getElementById("pr-1").style.left="100vw";
                             document.getElementById("pr-2").style.left="0vw";
                             document.getElementById("pr-3").style.left="200vw";
                             setActive1(true);
                             setActive2(false);
                             setActive3(false);
                          }}>Html|Css|Js</div>
                          <div className={"content2 " + (getActive2 && "active2")} onClick={()=>{
                             document.getElementById("pr-2").style.left="100vw";
                             document.getElementById("pr-1").style.left="0vw";
                             document.getElementById("pr-3").style.left="200vw";
                             setActive2(true);
                             setActive1(false);
                             setActive3(false);
                          }}>ReactJS</div>
                          <div className={"content3 " + (getActive3 && "active3")} onClick={()=>{
                             document.getElementById("pr-3").style.left="100vw";
                             document.getElementById("pr-2").style.left="200vw";
                             document.getElementById("pr-1").style.left="0";
                             setActive1(false);
                             setActive2(false);
                             setActive3(true);
   
                          }}>NodeJS|MDB</div>
                      </div>
                      <div className="layer3">
                          <div className="pr-1" id="pr-1">
                                   <Card text={arr[0]} img={img1}/>
                                   <Card text={arr[1]} img={img2}/>
                                   <Card text={arr[2]} img={img3}/>
                            </div>
                            <div className="pr-2" id="pr-2">
                                    <Card text={arr[3]} img={img4} />
                                    <Card text={arr[4]}  img={img5}/>
                            </div>
                            <div className="pr-3" id="pr-3">
                                    <Card text={arr[5]} />
                                    <Card text={arr[6]} />
                            </div>

                      </div>
              </div>
        </div>
        </>
    );
}
