import React from 'react';
import './Button.css';
export default function Button(props){

    var a="",b="";

    const sum = (num,opr)=>{
          var res = 0;
         if(opr === "+")
         res=res+num;
         else if(opr==="-")
         res=res-num;
         else if( opr === "×")
         res=res*num;
         else if( opr === "÷")
         res=res/num;

         return res;
    }
   const seperator = (string)=> {
       let res="",finalRes=0;
     let arr = Array.from(string);
     arr.map((item,i) =>{
         var num1=0,num2=0;

         if(item === "+"|item === "-"|item === "×"|item === "÷"|i== arr.length-1){
            finalRes = finalRes + sum(parseFloat(res),item);
               res="";
         }
         else
             res=res+item;
     })
       return finalRes;
   }

   


    return(
        <>
            <div className="btn-layer">
                 <div className="btn" id={props.text1} style={{color:`${props.color}`}} onClick={() =>{
                   var a =  document.getElementById(`${props.text1}`).innerHTML;
                   var b = document.getElementById('top').innerHTML;
                   document.getElementById('top').innerHTML = b+a;
                  console.log(seperator(b+a));
                 }}>{props.text1}</div>
                 <div className="btn" id={props.text2}  style={{color:`${props.color}`}} onClick={() =>{
                    var a = document.getElementById(`${props.text2}`).innerHTML;
                    var b = document.getElementById('top').innerHTML;
                    document.getElementById('top').innerHTML = b+a;
                    seperator(b+a);
                 }}>{props.text2}</div>
                 <div className="btn" id={props.text3}  style={{color:`${props.color}`}} onClick={() =>{
                    var a= document.getElementById(`${props.text3}`).innerHTML;
                    var b = document.getElementById('top').innerHTML;
                    document.getElementById('top').innerHTML = b+a;
                    seperator(b+a);
                 }}>{props.text3}</div>
                 <div className="btn" id={props.text4}  style={{color:`${props.colorText}`,backgroundColor:`${props.bg}`}} onClick={() =>{
                    var a= document.getElementById(`${props.text4}`).innerHTML;
                    var b = document.getElementById('top').innerHTML;
                    document.getElementById('top').innerHTML = b+a;
                    seperator(b+a);
                 }}>{props.text4}</div>
            </div>
        </>
    )
}
