import React, { useEffect, useRef } from "react";
import './Intro.css';
import { init } from "ityped";
import img from '../../images/coding.png';
import arrow from '../../images/arrow-right.png';

export default function Intro(){
    const textRef = useRef();
    useEffect(() =>{
        init(textRef.current,{
            showCursor: false,
            strings: ["FrontEnd...","BackEnd..."]
        });
    }, [])

    return(
        <>
        <div className="intro" id="intro">
            <div className="left">
                <h3>Hii, There I'm </h3>
                <h1>Kaif Siddique</h1>
                <h2>Web Developer <span ref={textRef}> </span> </h2>
                <img src={arrow} alt="" />
            </div>
            <div className="right">
                <div className="wrapper"> 
                <img src={img} className="img" alt="image here" />
                </div>
               
            </div>
        </div>
        </>
    );
}
