import React from "react";
import ReacrDOM from "react-dom";
import SideBar from "../SideBar/SideBar";
import Header from "../Header/Header";
import SearchIcon from '@mui/icons-material/Search';
import Card from "../MainComp/Body/Card/Card";
import "./Search.css"
import { useState } from "react";

export default function Search(){

  const [num,setNum]= useState(0);

  const getText= async (event)=>{

    const add=event.target.value;

    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': 'd23387d0c7mshfac0ad57eb3617dp18f03ajsn1f371fe043b4',
        'X-RapidAPI-Host': 'shazam.p.rapidapi.com'
      }
    };

    const url = "https://shazam.p.rapidapi.com/search?term="+`${add}`+"&locale=en-US&offset=0&limit=5";
    const res= await fetch(url,options);
    let data = await res.json();
   const img = data.tracks.hits[num].track.images.background;
   const title = data.tracks.hits[num].track.title;
   const artist = data.tracks.hits[num].track.subtitle;
   const src = data.tracks.hits[num].track.hub.actions[1].uri;
    // document.getElementById("result").appendChild(<Card img={img} title={title} artist={artist} src={src} />)

  }
     

    return(
        <>
          <div className="main">
            <div className="search">
               <div className="input-box">
                   <input type="text" className="text-search" placeholder="Search Music" onChange={getText}/>
                   <SearchIcon className="s-icon" fontSize="large" sx={{color:"white"}} />
               </div>
               <div className="result" id="result">
                  <h1 id="text" style={{color:"rgb(66, 65, 65)"}}>Search Your Music Here !!</h1>
               </div>
            </div>
            <Header />
            <SideBar />
          </div>
        </>
    )
}






