import React from 'react';
import Card from './Card';
import './skill.css';
import img1 from '../../images/web-site.png';
import img2 from '../../images/css-file.png';
import img3 from '../../images/javascript.png';
import img4 from '../../images/science.png';
import img5 from '../../images/nodejs.png';
import img6 from '../../images/mongodb-logo.svg';
export default function Skill(){

 const txt=["UI is most important in any website as it attracts the user . I use Figma to create beautiful designs , Canva etc;"," HTML is the base of any WebSite and CSS is important for the UI","JavaScript is very important to make any Website Fully Functional","React is the best for FrontEnd as it reduces the repetative code and makes website more beautifull","Node Js is one of the important BackEnd Technology which is easy and fast","MongoDB is used by Many Companies as it is one of the best DataBase"];
 const title=["Web-Design|UI","HTML|CSS","Javascript","React","NodeJs","MongoDB"];
    return(
        <>
        <div className="skills">
            <h1>Services</h1>
            <h3>I Design, I code, I Create - Its that simple</h3>
            <div id='sTop' className="top">
                <Card image={img1} title={title[0]} text={txt[0]} />
                <Card image={img2} title={title[1]} text={txt[1]} />
                <Card image={img3} title={title[2]} text={txt[2]} />
            </div>
            <div id='sBotm' className="bottom">
                <Card image={img4} title={title[3]} text={txt[3]} />
                <Card image={img5} title={title[4]} text={txt[4]} />
                <Card image={img6} title={title[5]} text={txt[5]} />
            </div>
            <div style={{
                width:"100%",
                position:"absolute",
                top:"95vh",
                display:"flex",
                justifyContent:"space-around",
                alignItems:"center"
            }}>
                <div id='c1' className='circles' onClick={()=>{
                    document.getElementById("sTop").style.left="0";
                    document.getElementById("sBotm").style.left="0";
                    document.getElementById("c1").style.backgroundColor="#009ffd";
                }}></div>
                <div id='c2' className='circles'></div>
                <div id='c3' className='circles'></div>
            </div>
        </div>
        </>
    )
}
