import React , {useState} from "react";
import './skill.css';
import img from '../../images/pexels-pixabay-270348.jpg';
import arrow from '../../images/arrow-right.png';
export default function Skill(){

    const [curslide,setSlide] = useState(0);
    const [active,setActive] = useState(true);
    const arr=[
       {
           id: 1,
           title: "HTML-CSS",
           level : "Intermediate"
       },
       {
           id: 2,
           title: "JavaScript",
           level : "Intermediate"
       },
       {
           id: 3,
           title: "React",
           level : "Intermediate"
       },
       {
           id: 4,
           title: "NodeJs",
           level : "Beginner"
       },
       {
           id: 5,
           title: "Express",
           level : "Beginner"
       },
       {
           id: 6,
           title: "MongoDB",
           level : "Beginner"
       }
      ]

    return(
        <>
        <div className="skills" id="skill">
            <h1>Skills</h1>
            <div className="arrow">
                    <div className="left">
                     <img src={arrow} onClick={()=>{
                       var result = (curslide>0)?curslide-1: arr.length-1;
                       setSlide(result);
                     }} />
                    </div>
                    <div className="img-right">
                    <img src={arrow} onClick={()=>{
                       var result = (curslide>=0 && curslide<5 )?curslide+1:0;
                       setSlide(result);
                     }} />
                    </div>
                </div>
            <div className={"container " + (active && "slider")}>
                <div className="left">
                    <h4>Skill :- {arr[curslide].title}</h4>
                    <h5>Level :- {arr[curslide].level}</h5>
                    <p>Check Projects <br>
                    </br>
                        <button>Check</button>
                    </p>
                </div>
                <div className="right">
                  <img src={img} alt="" />
                </div>
            </div>
            <div className="slider">
                <div className="left">
                    <h4>Skill :- {arr[curslide].title}</h4>
                    <h5>Level :- {arr[curslide].level}</h5>
                    <p>Check Projects <br>
                    </br>
                        <button>Check</button>
                    </p>
                </div>
                <div className="right">
                  <img src={img} alt="" />
                </div>
            </div>
        </div>
        </>
    );
}
