import React, { useState } from "react";
import './contacts.css';
import img from '../../images/contact.svg';

export default function Contacts(){

    const [email,setEmail] = useState("");
    const [text,setText] = useState("");

  function gettxt(event){
      setText(event.target.value);
  }
  function getemail(event){
       setEmail(event.target.value);
  }

    return(
        <>
        <div className="contacts" id="contacts">
            <div className="left">
                <img src={img} alt="" />
            </div>
            <div className="right">
                 <div className="form">
                     <h1 className="h1">Contact Me</h1>
                     <input type="email" id="input" placeholder="  Email" onChange={getemail} />
                     <textarea name="text" id="text" placeholder=" Type message" cols="20" rows="7" onChange={gettxt}></textarea>
                     <button type="submit" onClick={()=>{ 
                         if(email==="" && text==="")
                         {
                             alert("Can't Take Empty Values");
                         }
                         else{
                            document.getElementById("reply").style.visibility="visible";
                            document.getElementById("input").value=""; 
                            document.getElementById("text").value="";
                         }
                     }}>Send</button>
                 </div>
                 <h2 id="reply" style={{
                     visibility:"hidden",
                     color:"green"
                 }}>Hii, I'll reply You ASAP!</h2>
            </div>
        </div>
        </>
    );
}
