import React, { useState }  from "react";
import  './index.css';



function App(){

   const [list,upList] = useState();
//    const [showlist,upshowList] = useState();
   const [arr,uparr] = useState([]);

    function Show(e){
           upList(e.target.value)
    }
    function show(){

        if(document.getElementById("text").value === "")
        {
            alert("Can't take Empty Value");
        }
         else{
           uparr((olditems) =>{
               return [...olditems,list];
           });
           document.getElementById("text").value = "";
        }
    }


    return(
        <div className="main">
        <div className="centre-div">
            <br/> <br />
            <div className="heading">
                <h1>Todo's List</h1>
            </div>
            <br />
            <div className="input-text">
                <input id="text" type="text" placeholder="Enter an item" onChange={Show} />
                <button onClick={show}>+</button>
            </div>
            <br />
            <div className="list-items">
                <ol>
                    {arr.map((val,i) =>{
                       return(
                           <>
                           <li>{val}  <span>
                           <button onClick={function del(){
                               remove(i);
                           }}> {i} </button>
                           </span></li>
                           
                           </>
                       );
                    })}
                </ol>
            </div>
            <br />
        </div>
        </div>
    )
}
 export default App;
