import React, { useState }  from "react";
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import TextField from '@mui/material/TextField';
import CardContent from '@mui/material/CardContent';
import AddIcon from '@mui/icons-material/Add';

const bull = (
  <Box
    component="span"
    sx={{ display: 'inline-block', mx: '2px', transform: 'scale(0.8)' }}
  >
    •
  </Box>
);

export default function BasicCard() {

  const [list,upList] = useState();
  //    const [showlist,upshowList] = useState();
     const [arr,uparr] = useState([]);
  
      function Show(e){
             upList(e.target.value)
      }
      function show(){
  
          if(document.getElementById("standard-basic").value === "")
          {
              alert("Can't take Empty Value");
          }
           else{
             uparr((olditems) =>{
                 return [...olditems,list];
             });
             document.getElementById("standard-basic").value = "";
          }
      }

  return (
    <Card className='main-div' >
      <CardContent>
        <h1 className='heading'>
          Todo's List
          </h1>
          <div className="input">
          <TextField  id="standard-basic" placeholder="Enter an item" variant="standard" onChange={Show} />
          <button className='btn' onClick={show}>
          <AddIcon />
          </button>
          </div>
      </CardContent>
     <br />
     <div className="list-items">
                <ol>
                    {arr.map((val,i) =>{
                       return(
                           <>
                           <li>
                           <span>
                           <button className="delete" onClick={() =>{
                               uparr((olditems) =>{
                                   return olditems.filter((arrElm,index) =>{
                                       return index !== i;
                                   })
                               })
                           }}> × </button>
                           </span>{val}</li>
                           
                           </>
                       );
                    })}
                </ol>
            </div>
    </Card>
    
  );
}
