import React, { useState } from "react";
import AddIcon from '@mui/icons-material/Add';
import SimplePaper from './Note';

export default function Content(){
    
    const [title,upTitle] = useState();
    const [Arr,upArr] = useState([]);
    const [text,upText] = useState();
    

    function showTitle(e){
        upTitle(e.target.value);
    }
    function showText(e){
        upText(e.target.value);
    }
    function change() {

        var title = document.getElementById("title").value;
        var text = document.getElementById("text").value;

        if(title === "" && text === "")
        {
            alert("Can't take Empty Value");
        }
         else{
           upArr((olditems) =>{
               return [...olditems,<SimplePaper />];
           });
           document.getElementById("title").value = '';
          text = "";
        }
    }

    return(
    <>
    <div className="main">
        <div className="textArea">
            <div className="textContent">
    <input type="text" name="" id="title" placeholder="Title" onChange={showTitle} />
    <textarea name="" id="text" cols="20" rows="8" placeholder="Enter some text" onChange={showText} ></textarea>
    </div>
    <button className="btn" onClick={change}>
        <AddIcon />
    </button>
    </div>
    </div>
    <div className="notes">
    {
        Arr.map((val,i) =>{
            return( 
            <SimplePaper t1={title} t2={text}/>
            );
        })
    }
    </div>
    </>
);
};
